import React, { useEffect } from 'react';
import axios from 'axios';

import { token1 } from '../token';
import WeekWeatherScraper from './WeekWeatherScraper';

const city = 'Cypr';
const url = `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${token1}&units=metric&lang=ru`;

export default function DayWeatherScraper() {
    const [weather, setWeather] = React.useState([]);

    useEffect(() => {
        axios.get(url)
            .then(response => {
                setWeather(response.data);
                // console.log(response.data);
            })
            .catch(error => { console.log(error) })
    }, []);

    return (
        <React.Fragment>
            <h1>Город: {weather.name}</h1>
            {weather.main ? <p>Температура: {weather.main.temp.toFixed()} °C</p> : null}
            {weather.weather ? <p>Небо: {weather.weather[0].description}</p> : null}
            {weather.main ? <p>Как будто: {weather.main.feels_like.toFixed()} °C</p> : null}
            {weather.main ? <p>Влажность: {weather.main.humidity} %</p> : null}
            {weather.wind ? <p>Скорость ветра: {weather.wind.speed.toFixed()} MPH</p> : null}
            {weather.coord ? <WeekWeatherScraper coord={weather.coord} /> : null}
            {/* <p>Координаты: Широта: {weather.coord.lat} Долгота: {weather.coord.lon}  </p>  */}
        </React.Fragment>
    )
}
