import React, { useState } from 'react';

import NowWeatherScraper from './NowWeatherScraper';

export default function Search() {
    const [city, setCity] = useState('Moskov');

    const onClick = () => {
        setCity(document.getElementById('cityname').value);
        <NowWeatherScraper city={city} />
        // console.log(document.getElementById('cityname').value);
    };

    return (
        <React.Fragment>
            <form>
                <label htmlFor="city">City:</label>
                <input type="text" id="cityname" name='city'></input>
                <input type="submit" value="Submit" onClick={onClick}></input>
            </form>
        </React.Fragment>
    );
}
