import React, { useEffect } from 'react';
import axios from 'axios';

import { token1 } from '../token';
import WeekWeatherScraper from './WeekWeatherScraper';

const city = 'Cyprus';
const url = `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${token1}&units=metric&lang=ru`;

export default function DayWeatherScraper() {
    const [dayweather, setDayWeather] = React.useState([]);

    useEffect(() => {
        axios.get(url)
            .then(response => {
                setDayWeather(response.data);
                // console.log(response.data);
            })
            .catch(error => { console.log(error) })
    }, []);

    return (
        <React.Fragment>
            <button>Input Country or City</button>
            <hr />
            <h1>Сегодня</h1>
            <h2>Город: {dayweather.name}</h2>
            {dayweather.main ? <p>Температура: {dayweather.main.temp.toFixed()} °C</p> : null}
            {dayweather.weather ? <p>Небо: {dayweather.weather[0].description}</p> : null}
            {dayweather.main ? <p>Как будто: {dayweather.main.feels_like.toFixed()} °C</p> : null}
            {dayweather.main ? <p>Влажность: {dayweather.main.humidity} %</p> : null}
            {dayweather.wind ? <p>Скорость ветра: {dayweather.wind.speed.toFixed()} MPH</p> : null}
            <hr />
            {dayweather.coord ? <WeekWeatherScraper coord={dayweather.coord} /> : null}
            <hr />
        </React.Fragment>
    )
}
