import React, { useEffect } from 'react';
import axios from 'axios';

import { token2 } from '../token';

export default function WeekWeatherScraper(props) {
    const [weather, setWeekWeather] = React.useState([]);

    const latitude = props.coord.lat
    const longitude = props.coord.lon

    const url = `https://api.openweathermap.org/data/2.5/forecast?lat=${latitude}&lon=${longitude}&appid=${token2}&cnt=7&units=metric&lang=ru`;

    useEffect(() => {
        axios.get(url)
            .then(response => {
                setWeekWeather(response.data);
                console.log(response.data);
            })
            .catch(error => { console.log(error) })
    }, []);

    return (
        <React.Fragment>
            <h1>Город: {weather.name}</h1>
            {weather.main ? <p>Температура: {weather.main.temp.toFixed()} °C</p> : null}
            {weather.weather ? <p>Небо: {weather.weather[0].description}</p> : null}
            {weather.main ? <p>Как будто: {weather.main.feels_like.toFixed()} °C</p> : null}
            {weather.main ? <p>Влажность: {weather.main.humidity} %</p> : null}
            {weather.wind ? <p>Скорость ветра: {weather.wind.speed.toFixed()} MPH</p> : null}
            {weather.coord ? <p>Координаты: Широта: {weather.coord.lat} Долгота: {weather.coord.lon}</p> : null}
        </React.Fragment>
    )
}
